DROP TABLE IF EXISTS `huijia_admin`;
CREATE TABLE `huijia_admin` (
  `userid` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(20) DEFAULT NULL,
  `password` varchar(32) DEFAULT NULL,
  `roleid` smallint(5) DEFAULT '0',
  `realname` varchar(50) NOT NULL DEFAULT '',
  `auth` text NOT NULL,
  `list_size` smallint(5) NOT NULL,
  `left_width` smallint(5) NOT NULL DEFAULT '150',
  PRIMARY KEY (`userid`),
  KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

INSERT INTO `huijia_admin` VALUES('1','admin','c3284d0f94606de1fd2af172aba15bf3','1','超级管理员','','10','150');

DROP TABLE IF EXISTS `huijia_block`;
CREATE TABLE `huijia_block` (
  `id` smallint(8) unsigned NOT NULL AUTO_INCREMENT,
  `type` tinyint(1) NOT NULL,
  `name` varchar(50) NOT NULL,
  `content` mediumtext NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

INSERT INTO `huijia_block` VALUES('1','2','幻灯图片1','/data/upload/image/201706/ff51a255030f222649726578b26306e6.jpg');
INSERT INTO `huijia_block` VALUES('2','2','幻灯图片2','/static/images/AboutUS.jpg');
INSERT INTO `huijia_block` VALUES('3','2','幻灯图片3','/static/images/News.jpg');
INSERT INTO `huijia_block` VALUES('4','3','关于我们_公司介绍','&lt;div style=&quot;font-size:16px; line-height:30px;&quot;&gt;\r\n&lt;br /&gt;\r\n	&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n		北京市快乐英语(Happykids English)是一家专业针对3-12岁少儿英语培训机构。\r\n	&lt;/p&gt;\r\n&lt;br /&gt;\r\n	&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n		遵循先”听说”后”读写”的母语学习方式，采用原版的绘本故事、原版小说及原版电影教学，培养孩子们的敏锐的耳朵，良好的英语思维及语感，大胆开口说英语，流利表达自己的观点和思想。彻底摆脱传统英语教学枯燥无味及哑巴英语！让孩子们在“玩中学，学中玩”不知不觉掌握英语这项交流工具，并利用英语学到更多的知识。\r\n	&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('5','3','关于我们-团队介绍','绘佳教育竭力建设优秀教育团队！每位教师，必须经过严格的培训及审核才可入岗。');
INSERT INTO `huijia_block` VALUES('6','3','课程体系_教学理念','&lt;div style=&quot;width:1000px;&quot;&gt;\r\n&lt;div style=&quot; float:left; width:458px; padding-right:40px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;font-size:18px;&quot;&gt;&lt;b&gt;通过绘本故事学英文&lt;/b&gt;&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;1. 孩子学习喜欢重复,在重复中体验安全感,在重复中学习与进步。比如动画片看很多遍，或是一个游戏玩很多遍。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;2. 孩子喜欢听故事，夸张、有趣、奇特的故事情节可以让他们乐不可支，兴趣盎然。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;3. 孩子们对节拍也比较敏感，孩子们在节奏感很强的音乐或儿歌响起时，身体会随着韵律快乐地动起来。当然，他们也有静的一面，精美的图画也能吸引他们的眼球。当他们发现有趣的东\r\n西时，他们会兴奋地指出来。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:502px;&quot;&gt;&lt;img src=&quot;/data/upload/image/201707/57ddd844dd6125b7793596d891a9b164.jpg&quot; width=&quot;502&quot; height=&quot;300&quot; alt=&quot;教学优势&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;clear:both;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('7','1','链接_幻灯图片1','http://www.huijia-edu.com/index.php?id=39');
INSERT INTO `huijia_block` VALUES('8','1','链接_幻灯图片2','http://www.huijia-edu.com/index.php?id=3');
INSERT INTO `huijia_block` VALUES('9','1','链接_幻灯图片3','http://www.huijia-edu.com/index.php?id=4');
INSERT INTO `huijia_block` VALUES('10','3','中心展示-教学环境','&lt;img src=&quot;/static/images/Environment.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;div class=&quot;desc&quot;&gt;\r\n	&lt;br /&gt;\r\n绘佳快乐英语秉承小班制授课，每班不超过10人。&lt;br /&gt;\r\n&lt;br /&gt;\r\n通过多媒体教学，每个教师均配备多媒体课件。&lt;br /&gt;\r\n&lt;br /&gt;\r\n丰富有趣的图片，给孩子们创造一个温馨的教学环境。\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('11','3','课程体系-教学优势','&lt;div style=&quot;width:1000px; font-size:14px; line-height:25px;&quot;&gt;\r\n&lt;div style=&quot;width:450px; float:left;&quot;&gt;&lt;img src=&quot;/data/upload/image/201707/6b2d9ec7dead2bfe1a9947521146e185.gif&quot; width=&quot;450&quot; height=&quot;450&quot; alt=&quot;快乐英语&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;width:510px; padding-left:40px; float:right;&quot;&gt;\r\n&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;1. 故事具有娱乐性，给语言教学增加情趣，避免其他教材的枯燥性。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;2. 原版绘本作者的母语是英语，从小让孩子接触地道的英文，有利于孩子们语感的培养，有利于增加较大的词汇量。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;3. 绘本语言中，重复性比较高，符合孩子们喜欢重复的特点。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;4. 图画是世界语言，绘本中精美的图片，不仅可以帮助孩子们理解故事的大意，还可以培养孩子们的审美能力。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;5. 拓展知识：通过图画看世界，学习自己生活圈外的东西和词语，学习日常用语。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;clear:both;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('12','3','国际游学','&lt;div style=&quot; width:1000px;  font-size:14px; line-height:26px;&quot;&gt;\r\n&lt;div style=&quot;float:left; width:280px;height:295px; padding-left:30px; padding-right:20px; &quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;在我们的西海岸行程中，将安排最具加州特色和美国特色的行程，让孩子与家长一同感受大洋彼岸的自然景色、人文风情、现代科技与娱乐气息。&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;我们这次的行程安排，是一次对美国西部的深度游览，带你看遍这些地点：&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:670px; height:295px;&quot;&gt;\r\n&lt;div id=demo style=&quot;overflow:hidden;width:670px; height:295px;&quot; align=center&gt;\r\n	&lt;table border=0 align=center cellpadding=1 cellspacing=1 cellspace=0 &gt;\r\n	&lt;tr&gt;&lt;td valign=top bgcolor=ffffff id=marquePic1&gt;\r\n	&lt;table width=\'100%\' border=\'0\' cellspacing=\'0\'&gt;\r\n    &lt;tr&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/9496c4987ce1c186f25517d254cd19a5.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/50d998e80362e29f789c9638ceb2de51.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/12e0424abaa2a56e4d4bcdf69fd1ba28.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/fd0b984de2bb843c275a221e9777903a.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/5da1031e461b3d1a92f94eff9e9ef851.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/c79eeece9a7d117a7222789a4506a2ca.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/b0aae243dd2dffd7229c3466c568f37d.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/cbff5399a3b4a4bb443ee1499da0ef53.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/ccab3ad48170eed2252674dbb0fa9e81.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/cce17b26d31f47cfde7b33ab3b63439a.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/db0767a2d7c7eb66bc95b6155c5da5e2.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/d97f8c2bb6da1490f69e7eb786ed2673.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;td align=center&gt;&lt;a href=\'#\'&gt;&lt;img src=&quot;/data/upload/image/201707/b112e797d8f5f5a94555694184ecfddd.jpg&quot; width=500 height=295 border=0&gt;&lt;/a&gt;&lt;/td&gt;\r\n    &lt;/tr&gt;&lt;/table&gt;\r\n	&lt;/td&gt;&lt;td id=marquePic2 valign=top&gt;&lt;/td&gt;&lt;/tr&gt;\r\n	&lt;/table&gt;&lt;/div&gt;\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\nvar speed=30 \r\nmarquePic2.innerHTML=marquePic1.innerHTML \r\nfunction Marquee(){ \r\nif(demo.scrollLeft&gt;=marquePic1.scrollWidth){ \r\ndemo.scrollLeft=0 \r\n}else{ \r\ndemo.scrollLeft++ \r\n} \r\n} \r\nvar MyMar=setInterval(Marquee,speed) \r\ndemo.onmouseover=function() {clearInterval(MyMar)} \r\ndemo.onmouseout=function() {MyMar=setInterval(Marquee,speed)} \r\n&lt;/script&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;clear:both; height:1px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('13','2','关于我们_公司介绍-图片','/static/images/co.jpg');
INSERT INTO `huijia_block` VALUES('14','3','关于我们_联系我们-地图','/data/upload/image/201706/26670e7233770bfac0fea20d654da5e5.png');
INSERT INTO `huijia_block` VALUES('15','3','路线行程','&lt;div style=&quot;width:1000px; line-height:25px;&quot;&gt;\r\n&lt;div style=&quot;width:1000px; text-align:center;&quot;&gt;&lt;p style=&quot;text-indent:2em; font-size:20px;&quot;&gt;&lt;b&gt;美国夏令营营地A（Camp James）&lt;/b&gt;&lt;/p&gt;\r\n&lt;hr /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot; float:left; width:560px; height:249px; padding-right:40px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em; font-size:18px;&quot;&gt;&lt;b&gt;营地介绍&lt;/b&gt;&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;自然环境：&lt;/b&gt;营地位于美国加州，坐落于Newport Beach（纽波特沙滩），周边是著名的加州富人区和度假胜地，依傍着一座海洋泻湖和一个巨大的泳池。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;专业认证：&lt;/b&gt;美国营地协会（ACA）认可营地，ACA是国际公认的营地权威，ACA认证意味着营地已成功通过数百种健康、安全、设施、人员和行业计划标准。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot; float:right; width:400px; height:249px;&quot;&gt;&lt;img src=&quot;/data/upload/image/201707/705de440eda251649bd5e321147af5e4.png&quot; width=&quot;400&quot; height=&quot;249&quot; alt=&quot;ACA认证&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot; clear:both; height:15px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px; line-height:25px;&quot;&gt;\r\n&lt;div style=&quot;float:left; width:500px; height:300px; padding-top:20px&quot;&gt;&lt;img src=&quot;/data/upload/image/201707/f72537dd87a2ef279136b5602dedab1d.jpg&quot; width=&quot;500&quot; height=&quot;278&quot; alt=&quot;夏令营&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:460px; height:320px; padding-left:40px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;师资力量：&lt;/b&gt;营地工作人员均为从事教育等领域经验丰富人士，包括发展心理学博士柯林斯、娱乐管理学专家Roach、拥有16年营地教育经验的科斯塔等专业人士，以及一大批有资格认证的辅导员。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地安全：&lt;/b&gt;除了严格的管理制度，营地配备有红十字会认证的专业急救人员；在进行诸多水上项目之前，露营者需参加简短的游泳测试；所有泻湖游泳、划船和水上乐园活动均配备救生衣。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地饮食：&lt;/b&gt;每日在营区享受一顿美味的午餐，背靠太平洋的营地会提供极具加州风味的美食，营养搭配科学合理，丰富而便捷。&lt;/p&gt;&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot; clear:both; height:25px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px; line-height:25px;&quot;&gt;\r\n&lt;div style=&quot;width:1000px; height:25px;text-align:center; font-size:18px; clear:both; margin-bottom:20px;&quot;&gt;&lt;b&gt;课程特色&lt;/b&gt;&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:298px; height:210px; border:#000 dashed 1px; padding-left:10px; padding-right:10px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;运动类：&lt;/b&gt;夏天，要让孩子们远离电视、电脑，拒绝变成“沙发土豆”，营地会帮你实现这一切——无论是在Gaga躲球游戏，还是神秘的魁地奇比赛，抑或在泻湖进行水上运动，都会让你进行酣畅淋漓的运动。营地还有一个专业武术指导员，翻滚教练课、迷你高尔夫和远足活动也能满足你。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:298px; padding-left:10px; padding-right:10px; margin-left:20px; margin-right:20px; height:210px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;探险类：&lt;/b&gt;随着儿童年龄的增长，他们对冒险越来越感兴趣。营地是进行探险的安全又理想的场所。在救生员的监护下，我们将开展各类海滨活动，包括划独木舟、皮划艇和立式桨板，这会让许多第一次尝试的孩子激动不已。充气水上乐园、射箭训练、沙滩车、真人CS等让孩子在勇气的鼓舞下不断成长。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:298px; padding-left:10px; padding-right:10px; height:210px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;创意与艺术类：&lt;/b&gt;通过舞蹈、戏剧、音乐和手工艺活动，孩子们有机会展示他们的创造力和个性。对很多孩子来说，这是他们第一次在舞台上或着在国外的观众面前表演。&lt;/p&gt;\r\n&lt;/div&gt; \r\n&lt;div style=&quot; clear:both; width:1000px; height:20px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot; width:1000px; height:210px;&quot;&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/cf3242e9bcd2cfb53b583ca9ee374315.jpg&quot; width=&quot;300&quot; height=&quot;200&quot; alt=&quot;夏令营&quot; /&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/8a02a329c8a06294eeb0a72e620af401.jpg&quot; width=&quot;300&quot; height=&quot;200&quot; alt=&quot;夏令营&quot;  style=&quot;margin-left:40px; margin-right:40px;&quot; /&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/22fc7ec89820a9932dfddbe4200d5ad0.jpg&quot; width=&quot;300&quot; height=&quot;200&quot; alt=&quot;夏令营&quot; /&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px; height:40px;&quot;&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px;&quot;&gt;\r\n&lt;div style=&quot;width:1000px; text-align:center;&quot;&gt;\r\n  &lt;p style=&quot;text-indent:2em; font-size:20px;&quot;&gt;&lt;b&gt;美国夏令营营地B（Pali营地）&lt;/b&gt;&lt;/p&gt;\r\n&lt;hr /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;width:980px; padding:0 10px; font-size:14px; line-height:25px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;始建于1990年Pali营地位于加州南部圣伯纳丁山脉上的派励峰上，紧邻有“加州小瑞士”之称的箭头湖，与著名旅游胜地“大熊湖”相距咫尺。该地区夏季凉爽，平均气温在12摄氏度至23摄氏度。营地占地面积约75英亩（约40万平方米），为森林和高山湖泊所环绕，营地海拔近1000米，环境优美，空气清新。该营拥有美国营地协会（American Camp Association）和美国西部独立营地协会(Western Association of Independent Camps)专业双认证，其多样的体育活动和艺术课程让次营地深受洛杉矶地区中产家庭的欢迎。&lt;/p&gt;&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:560px; height:300px; padding-right:40px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地类型：&lt;/b&gt;全封闭式传统营&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地地址：&lt;/b&gt;30778 Highway 18,Running Springs,CA 92382&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;学员组成：&lt;/b&gt;往届学员50%来自加州，35%来自北美其他各州，15%来自海外，所有入营学员将会拆散分组，全面融入美国学生群体。&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;适合年龄段：&lt;/b&gt;8-16岁（适合具有基本英语沟通能力，热爱艺术，敢于接受挑战，喜爱集体生活的孩子）&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;参营时间：&lt;/b&gt;7天&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:400px;&quot;&gt;&lt;img src=&quot;/data/upload/image/201707/d5f94ca0aca0475e35fd333709f8f46c.jpg&quot; width=&quot;400&quot; height=&quot;300&quot; alt=&quot;Pali夏令营&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;clear:both; width:1000px; height:30px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px;&quot;&gt;\r\n&lt;div style=&quot;width:400px; float:left; height:660px;&quot;&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/c616885cb722a9ba202d5a26a41d7390.jpg&quot; width=&quot;400&quot; height=&quot;300&quot; alt=&quot;Pali夏令营&quot; /&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/a814beb4eb61eb3b213cf2822473d3e9.jpg&quot; width=&quot;400&quot; height=&quot;300&quot; alt=&quot;Pali夏令营&quot; style=&quot;margin-top:25px;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;width:560px; float:right; height:660px; padding-left:40px;&quot;&gt;\r\n&lt;br /&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地辅导员：&lt;/b&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;营地的辅导员大约一半是美国辅导员，一半是国际辅导员，主要来自英国、澳大利亚等地方。该营地为了确保营员的安全，对辅导员的挑选非常严格，成为该营地的辅导员需要经过电话面试、面试、推荐人制度和背景调查，背景调查包括犯罪记录调查和毒品测试。而优秀的营地也会吸引更多高质量的辅导员，很多优秀辅导员都是每年夏天去营地报到。该营地的辅导员复报率达70%。\r\n营地以其多年积累的丰富经验，对营地辅导员进行专业训练。从儿童心理学、安全措施、项目技巧、沟通技巧等方面全面培训辅导员。&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地安全：&lt;/b&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;营地有24小时监控的门禁系统，有2个24小时值班的护士，1个随传随到的医生，最近的医院距离营地开车仅需4分钟，每位辅导员也都接受过红十字会组织的CPR和急救训练；\r\n营地提供北美意外保险，实时与内部工作人员沟通，每天晚间在家长群内更新学员当天状况。&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地饮食：&lt;/b&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;营地餐厅的主厨是经验丰富屡获殊荣的，他带领他的团队在2600平米的餐厅中为营员提供健康、营养、美味的一日三餐，还包括自助沙拉、素食、自制甜点、山泉水、茶，与热巧克力。如果有特殊餐饮要求可在入营的时候提出，营地会尽量满足。用餐时每桌都有辅导员组织大家在愉快友好热闹的氛围中用餐。&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;营地住宿：&lt;/b&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;舒适的宿舍配备双层实木床、个人物品柜、纯净山泉水、带淋雨的卫生间。每间宿舍都有辅导员陪同。&lt;/p&gt;\r\n\r\n&lt;/div&gt;\r\n&lt;div style=&quot;width:1000px; clear:both; height:30px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px; line-height:25px;&quot;&gt;\r\n&lt;div style=&quot;width:980px; padding-left:10px; padding-right:10px; clear:both; margin-bottom:20px;&quot;&gt;\r\n&lt;p style=&quot;font-size:18px; text-align:center;&quot;&gt;&lt;b&gt;课程特色&lt;/b&gt;&lt;/p&gt;&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;毗邻电影之都，好莱坞的热情似乎已经感染了这座营地，与一群热爱运动，崇尚艺术的美国当地青少年在一起，度过难忘的盛夏时光，或许将成为他人生中最难忘的童年回忆。&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;营地共设有6项主修课程，超过70项的选修课程，以及11项夜间主题活动。在营地生活中，学员们可以根据自己的兴趣爱好自由选择：包含艺术类、表演类、运动挑战类等。\r\n&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:298px; height:200px; border:#000 dashed 1px; padding-left:10px; padding-right:10px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;艺术类：&lt;/b&gt;或许是因为营地临近好莱坞，这里的一切都充满潮流艺术气息。学习拍一段属于自己的独立电影，玩转电影特效化妆，拿起颜料罐创作一副充满想象力的街头涂鸦，孩子们体内的艺术细胞在这里将被充分激发！&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:298px; padding-left:10px; padding-right:10px; margin-left:20px; margin-right:20px; height:200px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;表演类：&lt;/b&gt;对于充满表现欲望的孩子，这里绝对是他们发挥演绎天赋的舞台。与一群天性开朗，充满美式热情的小伙伴在一起，重现一场电影桥段，模拟一次新闻采访，或是开上一场摇滚演唱会，每一种表演天赋，都能在这里得到发挥。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:298px; padding-left:10px; padding-right:10px; height:200px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;挑战类：&lt;/b&gt;如果您的孩子对于冒险挑战兴致浓厚，将是个绝佳选择。在充足的安全保护下体验空中飞人的乐趣，好莱坞级别的特技演绎绝对会让孩子们收获难忘的刺激体验。&lt;/p&gt;\r\n&lt;/div&gt; \r\n\r\n&lt;div style=&quot;clear:both; width:1000px; height:20px;&quot;&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;float:left; width:298px; height:210px; border:#000 dashed 1px; padding-left:10px; padding-right:10px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;辅导员培训：&lt;/b&gt;在特色课程以及选修课程之外，想培养领导力的学员还可选择CIT课程。学员可获得志愿者服务时间，参与各类实践活动与研讨会，学习在绳索课程中如何保护他人，学习急救知识与技巧并获得专业认证，为营地内的年轻学员提供协助，激发学员对于领导力的热情。&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:left; width:298px; padding-left:10px; padding-right:10px; margin-left:20px; margin-right:20px; height:210px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;选修课程：&lt;/b&gt;特色课程之外，更有超过70项选修课程！足够丰富的营地设施为学员们提供更多选择。从刺激的越野摩托车，到安静凝神的瑜伽课程，让学员们的营地生活更加充实。\r\n&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:298px; padding-left:10px; padding-right:10px; height:210px; border:#000 dashed 1px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;b&gt;晚间活动：&lt;/b&gt;夜幕降临之后，营地将会迎来新一场的狂欢。欢迎篝火、歌舞模仿、主题舞会等11种晚间活动，将为学员们带去一个个色彩斑斓的夜晚。\r\n&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;clear:both; width:1000px; height:20px;&quot;&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;width:1000px; height:418px;&quot;&gt;\r\n&lt;img src=&quot;/data/upload/image/201707/a45be2f0c8cb1f5441f58459435ceb6d.jpg&quot; width=&quot;1000&quot; height=&quot;418&quot; alt=&quot;游学&quot; /&gt;&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('16','3','游学收获','&lt;div style=&quot;width:900px; margin:auto; font-size:16px;&quot;&gt;\r\n&lt;div style=&quot;float:left; width:400px;&quot;&gt;\r\n&lt;img src=&quot;/data/upload/image/201706/9eb174daf14fd1cb058364ee90bdc602.jpg&quot; width=&quot;400&quot; height=&quot;320&quot; alt=&quot;绘佳教育&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:470px; padding-left:30px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;纯英语环境下的锻炼，让孩子平常所学能够学以致用，让外语真正成为一个交流的工具。\r\n&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p&gt;在沟通、社交和自理能力上得到提高，每天丰富多彩的户外运动，远离课本和补习班。在玩中学会学。\r\n&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;\r\n&lt;p&gt;游学于美国，深入接触多元的世界文化，让孩子真正成为一个具备国际视野的人。\r\n&lt;/p&gt;&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;clear:both; height:1px;&quot;&gt;&lt;/div&gt;\r\n\r\n&lt;/div&gt;');
INSERT INTO `huijia_block` VALUES('17','3','报名方式','&lt;div style=&quot;width:900px; margin:auto; font-size:16px;&quot;&gt;\r\n&lt;div style=&quot;float:left; width:550px;&quot;&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;上地校区地址：北京市海淀区上地东路35号颐泉汇1号楼106室/2号楼118室&lt;/p&gt;&lt;br /&gt;\r\n&lt;p&gt;咨询电话：010-82157866 56136990&lt;/p&gt;&lt;br /&gt;\r\n&lt;p&gt;百旺校区地址：北京市海淀区后厂村路85号快乐英语（冬晴园底商）&lt;/p&gt;&lt;br /&gt;\r\n&lt;p&gt;咨询电话：010-62730046 13521477132&lt;/p&gt;&lt;br /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;float:right; width:350px;&quot;&gt;&lt;img src=&quot;/data/upload/image/201706/452ce0c0c8b4730f0a7b1457ef9cce77.jpg&quot; width=&quot;350&quot; height=&quot;222&quot; alt=&quot;绘佳教育&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;clear:both; height:1px;&quot;&gt;&lt;/div&gt;\r\n\r\n&lt;/div&gt;');

DROP TABLE IF EXISTS `huijia_category`;
CREATE TABLE `huijia_category` (
  `catid` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `typeid` tinyint(1) NOT NULL,
  `modelid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `parentid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `child` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `childids` varchar(255) NOT NULL,
  `catname` varchar(30) NOT NULL,
  `image` varchar(100) NOT NULL,
  `content` mediumtext NOT NULL,
  `seo_title` varchar(255) NOT NULL,
  `seo_keywords` varchar(255) NOT NULL,
  `seo_description` varchar(255) NOT NULL,
  `catdir` varchar(30) NOT NULL,
  `http` varchar(255) NOT NULL,
  `items` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `listorder` smallint(5) unsigned NOT NULL DEFAULT '0',
  `ismenu` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `ispost` smallint(2) NOT NULL,
  `verify` smallint(2) NOT NULL DEFAULT '0',
  `islook` smallint(2) NOT NULL,
  `listtpl` varchar(50) NOT NULL,
  `showtpl` varchar(50) NOT NULL,
  `pagetpl` varchar(50) NOT NULL,
  `pagesize` smallint(5) NOT NULL,
  PRIMARY KEY (`catid`),
  KEY `listorder` (`listorder`,`catid`)
) ENGINE=MyISAM AUTO_INCREMENT=32 DEFAULT CHARSET=utf8;

