DROP TABLE IF EXISTS `huijia_content`;
CREATE TABLE `huijia_content` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `modelid` smallint(5) NOT NULL,
  `title` varchar(80) NOT NULL DEFAULT '',
  `thumb` varchar(255) NOT NULL DEFAULT '',
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `description` varchar(255) NOT NULL,
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '1',
  `hits` smallint(5) unsigned NOT NULL DEFAULT '0',
  `username` char(20) NOT NULL,
  `time` int(10) unsigned NOT NULL DEFAULT '0',
  `level` tinyint(2) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `catid` (`catid`,`listorder`,`time`),
  KEY `time` (`catid`,`time`),
  KEY `status` (`catid`,`status`)
) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;

INSERT INTO `huijia_content` VALUES('1','13','2','小布丁故事英语','/data/upload/image/201706/dddb5642bd0179eaeea1c6586e66119d.jpg','英语,故事','利用小布丁故事英语教学，培养孩子对英语的兴趣。','0','1','0','admin','1493800483','0');
INSERT INTO `huijia_content` VALUES('2','13','2','原版英文绘本教程','/data/upload/image/201706/6b82d63244a20eeebac30dcfe686b262.jpg','英文','188课时/每年，分为春季学期、秋季学期、暑期集训、寒假集训。每课时45分钟。','0','1','0','admin','1493800534','0');
INSERT INTO `huijia_content` VALUES('3','13','2','原版英文小说','/data/upload/image/201706/8f3f55d600fde3aecd710ff327131ded.jpg','英文小说','学习与小布丁故事相关的词汇，采用看图说话的方式，直接应用所学内容，这一部分是课程介绍的详细内容','0','1','0','admin','1493800653','0');
INSERT INTO `huijia_content` VALUES('4','13','2','剑桥青少','/data/upload/image/201706/90a75f4310795616534405ca8080c88a.jpg','KET,PET,剑桥','《剑桥英语（青少版）》，简称《剑桥青少》，由英国剑桥大学、教育\r\n部高等教育出版社、教育部《国家英语课程标准》（新课标）专家组成\r\n员倾力打造，是当今国内唯一一套系统学习和考级应试性完美结合的青\r\n少英语教材。','0','1','0','admin','1493800863','0');
INSERT INTO `huijia_content` VALUES('5','13','2','原版小说阅读会','/data/upload/image/201707/0e81705ae78e1cf762fecf8566f303ba.jpg','小说阅读','采用美国顶尖原版教材结合精选的同级别精读原版书，依照“全美英语标准大纲（Common Core State Standards）” 并结合中国学生的实际特点进行本土化研发，使学生能够真正运用英语，用英语去思考，去探索，去了解各国的文化水平。','0','1','0','admin','1493800910','0');
INSERT INTO `huijia_content` VALUES('6','24','1','想学好英语，先叫醒“耳朵”。','/data/upload/image/201706/d19d36e2b7db1051c30050fe8d2184a5.jpg','动态','想学好英语，先叫醒“耳朵”。为什么一定要先培养孩子的听力?','0','1','0','admin','1493806314','0');
INSERT INTO `huijia_content` VALUES('7','22','1','绘佳快乐英语给大家送祝福啦!','/data/upload/image/201706/f0aae242e4e0fd0bb265ac0aad87ed7e.jpg','动态','正月十五吃元宵，在我国是由来已久习俗。人们在元宵节吃汤圆，实际上是思念亲人、渴望团圆的意思。','0','1','0','admin','1493806417','0');
INSERT INTO `huijia_content` VALUES('8','9','7','市场专员','','','\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n职位月薪：4000-6000元\r\n工作地点：北京-海淀区\r\n工作性质：全职工作\r\n\r\n\r\n经验：不限\r\n最低学历：大专\r\n招聘人数：1人\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n岗位职责：\r\n1）完成校区月度/季度市场业绩\r\n2）负','0','1','0','admin','1493821228','0');
INSERT INTO `huijia_content` VALUES('9','18','1','Wendy','/data/upload/image/201706/facb495b34c7b6eae8c5297e3f36147b.jpg','教师风采','Wendy老师活泼可爱，热情open，喜欢孩子，喜欢教 育，对孩子有耐心有爱心，推崇尊重孩子的天性进行 教育，对不同孩子因材施教。课上肢体动作语言丰富 夸张，深的孩子们的喜爱。','0','1','0','admin','1493879397','0');
INSERT INTO `huijia_content` VALUES('10','19','1','郑羿菲','/data/upload/image/201706/51f5c9a27ed02897201153522bfceb4c.jpg','明星学员','Elina在绘佳快乐英语学习一年多以来，一直都很努力，也很认真！','0','1','0','admin','1493879513','0');
INSERT INTO `huijia_content` VALUES('11','24','1','幼儿园宝贝不可错过的英语启蒙！','/data/upload/image/201706/a0a02976e3d0b14aaa830606d024037f.jpg','幼儿园,英语,宝贝','世界上有两大语系，一个是表音语语，系一个是表意语系，中文属于表意，英语及欧美大部分国家是表意的。','0','1','0','admin','1493880050','0');
INSERT INTO `huijia_content` VALUES('13','18','1','Anna','/data/upload/image/201706/adc34f7ff8a124fd55fa77053f96d8cd.jpg','名师风采','Anna老师','0','1','0','admin','1494313679','0');
INSERT INTO `huijia_content` VALUES('14','18','1','Yolanda','/data/upload/image/201706/0e56aa05909cf40d88fbd7ca6f7d4dec.jpg','名师风采','毕业于河南师范大学，获得全国统考教师资格证，英语专业八级','0','1','0','admin','1494404572','0');
INSERT INTO `huijia_content` VALUES('15','30','8','绘佳快乐英语上地校区','/data/upload/image/201705/82b1749a7262ff619920a918a862820c.jpg','快乐英语','绘佳快乐英语上地校区成立于2011年，位于颐泉汇1号楼106室，后增设2号楼118室。几年来，始终致力于发展少儿英语教育，努力打造成为上地周边优质的英语专业教育机构。','0','1','0','admin','1495363632','0');
INSERT INTO `huijia_content` VALUES('16','9','7','英语教师','','','\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n职位月薪：6000-10000元\r\n工作地点：北京-海淀区\r\n工作性质：全职工作\r\n\r\n\r\n经验：不限\r\n最低学历：不限\r\n招聘人数：5人\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n岗位职责：\r\n加入绘语时光大家庭，您将得到：\r\n1.','0','1','0','admin','1497235846','0');
INSERT INTO `huijia_content` VALUES('17','18','1','Rella','/data/upload/image/201706/e2b6745b7dccc107ef717025d10308d4.jpg','名师风采','获得英语教育硕士学位，从事英语教育7年。并于2015年在美国田纳西大学-诺克斯维尔孔子学院学术交流进修。','0','1','0','admin','1497240681','0');
INSERT INTO `huijia_content` VALUES('19','19','1','史婉哲','/data/upload/image/201706/8ba46023166e8174af03b0bcca344e08.jpg','明星学员','“光阴似箭，无折无返”，转眼Angel已经在绘佳快乐英语学习三年了。三年的学习过程有苦有乐，有爸爸妈妈，姥姥姥爷的无私养育，更有Candy、Jenny和Allison的涓涓教诲。','0','1','0','admin','1497240833','0');
INSERT INTO `huijia_content` VALUES('23','30','8','绘佳快乐英语百旺校区','/data/upload/image/201705/12eb4408b9e306041ce7a5b16eafc250.jpg','快乐英语','绘佳快乐英语百旺校区位于中关村二小百旺分校北门正对面。现有5间教室，教职人员10余人。','0','1','0','admin','1497241025','0');
