INSERT INTO `huijia_content` VALUES('28','23','1','绘佳教育•快乐英语主题活动【蛋糕趴】精彩回顾','/data/upload/image/201706/0b47db5736d58e1a159387f27e6550fe.jpg','','\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;孩子们一大早上起来早早就兴奋地来到了现场，虽然有点霾，但丝毫不阻止孩子们活跃的神经，一个个跑跑跳跳亢奋不已。\r\n\r\n\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;n','0','1','0','admin','1497599533','0');
